import './App.css';
import React,{useState} from "react";

function App(){
  const [number,SetNumber]=useState(0);
  const [name,setName] =useState("");
  const [pop,setPop] = useState("");
  let status ="false";
  const add =(increase)=>{

    SetNumber(number+1);
    if(number==14){
      setPop("Great Work!");
    }
    else{
      setPop("");
    }
  };
  const sub=(decrease)=>{
    decrease.currentTarget.disabled = {status};
    if(number<1){
      decrease.currentTarget.disabled = true;
    }
    else{
    SetNumber(number-1);
    if(number==16){
      setPop("Great Work!");
    }
    else{
      setPop("");
    }
    }
  };


  return(
    <div className="App">
      <div>
        <h2>{name}</h2>
      <h1>{number}</h1>
      <button onClick={(e)=> add(e)}>+</button>
      <button onClick={(e)=> sub(e)}>-</button>
    <h3>{pop}</h3>
    </div>
    </div>
  );
}

export default App;
