import React from "react";
const Card=(props)=>{
    return(
        
        <div>
            style={{
                borderRadius:"5px",
                padding: "2rem",
                border:"2px",
                borderColor:"black",
                borderStyle:"solid",
                margin:"3rem",
            }}
            <h1 style={{color:"blue"}}>{props.name}</h1>
            <p>{props.descr}</p>
            <button onClick={props.buttonOnClick} >
            {props.buttonName}
            </button>
            
        </div>

    );
};

export default Card;
