import './App.css';
import React,{useState} from "react";

function App(){
  const [number,SetNumber]=useState(0);
  const [name,setName] =useState("");
  const add =(increase)=>{
    SetNumber(number+1);
  };
  const sub=(decrease)=>{
    if(number>0){
      decrease.currentTarget.disabled = false;
    }
    else{
      decrease.currentTarget.disabled = true;
    }
    SetNumber(number-1);
  };
  const changeName =()=>{
    setName("Yuvraj");
  };

  return(
    <div className="App">
      <div>
        <h2>{name}</h2>
      <h1>{number}</h1>
      <button onClick={(e)=> add(e)}>+</button>
      <button onClick={(e)=> sub(e)}>-</button>
    <button onClick={(e)=>changeName()}>$</button>
    </div>
    </div>
  );
}

export default App;
