import './App.css';
import React,{useState} from "react";
import Card from "./Components/card";

function App(){
  const [number,SetNumber]=useState(0);

  const [pop,setPop] = useState("");
  const add =(increase)=>{

    SetNumber(number+1);
    if(number==14){
      setPop("Great Work!");
    }
    else{
      setPop("");
    }
  };
  const sub=(decrease)=>{
    SetNumber(number-1);
    if(number==16){
      setPop("Great Work!");
    }
    else{
      setPop("");
    }
  };


  return(
    <div className="App">
      <div>

      <h1>{number}</h1>
      <button  onClick={(e)=> add(e)}>+</button>
      <button disabled={number===0} onClick={(e)=> sub(e)}>-</button>
    <h3>{pop}</h3>
    <Card name={"Add"} descr={"add"} buttonName="Add" buttonOnClick={()=>{SetNumber(number+1)}}/>
    <Card name={"Sub"} descr={"sub"} buttonName="Sub" buttonOnClick={()=>{SetNumber(number-1)}}/>

    </div>
    </div>
  );
}

export default App;
