import './App.css';
import React,{useState} from "react";

function App(){
  const [number,SetNumber]=useState(0);
  const [name,setName] =useState("");
  const [pop,setPop] = useState("");
  const add =(increase)=>{
    SetNumber(number+1);
    if(number==15){
      setPop("Great Work!");
    }
  };
  const sub=(decrease)=>{

    if(number<0){
      decrease.currentTarget.disabled = true;
    }
    else {decrease.currentTarget.disabled = false;
    SetNumber(number-1);
    }
  };


  return(
    <div className="App">
      <div>
        <h2>{name}</h2>
      <h1>{number}</h1>
      <button onClick={(e)=> add(e)}>+</button>
      <button onClick={(e)=> sub(e)}>-</button>
    <h3>{pop}</h3>
    </div>
    </div>
  );
}

export default App;
