package Introduction.Loops;
import java.util.*;

public class Program2 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(char i = 65 ; i<70; i++){
            for(char j=65; j<=i ; j++){
                System.out.print(j);
            }
            System.out.println();
		}   

	}
}
